sys_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below
	var addnumber = new GlideAjax('clientside');
	addnumber.addParam('sysparm_name','addnum');
// 	alert("NUm1:"+ g_form.getIntValue('num1'));
	addnumber.addParam('sysparm_usr_num1', g_form.getIntValue('number1'));
	addnumber.addParam('sysparm_usr_num2', g_form.getIntValue('number2'));

	addnumber.getXML(populateResult);



	function populateResult(response){
		// Extract the email address from the response, clear any value from the email field, 
		// set new value in the email field
		var sumResult = response.responseXML.documentElement.getAttribute("answer");
// 		g_form.clearValue('result');
		alert("Sum Result"+sumResult);

		g_form.setValue('result',parseInt(sumResult));
	}
}
