sys_script_include.script:
var clientside = Class.create();
clientside.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	addnum: function() {
		var num1 = this.getParameter('sysparm_usr_num1');
		var num2 = this.getParameter('sysparm_usr_num2');
		var n1=parseInt(num1);
		var n2=parseInt(num2);
		gs.include('addnumscript');
		var add=new addnumscript();
		var result=add.add(n1,n2);
// 		gs.info("Rslt"+result);
		return result;
	},
    type: 'clientside'
});
